import React, { useState, useContext, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import api from "../api/axiosConfig";
import { UserContext } from "../context/UserContext";

export default function WritePost() {
  const { id } = useParams(); // optional post id for edit
  const isEdit = Boolean(id);
  const { user, isAuthenticated } = useContext(UserContext);
  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [fetching, setFetching] = useState(false);
  const [error, setError] = useState("");
  const [unauthorized, setUnauthorized] = useState(false);

  useEffect(() => {
    if (!isEdit) return;
    let mounted = true;
    const fetchPost = async () => {
      setFetching(true);
      setError("");
      try {
        const res = await api.get(`/api/posts/${id}`);
        if (!mounted) return;
        const post = res.data;
        setTitle(post.title || "");
        setContent(post.content || "");

        // Determine authorization: current user must be author or admin
        const author = post.author || {};
        const authorId = author._id || author.id || author;
        const currentUserId = user ? (user._id || user.id) : null;
        const isAuthor = currentUserId && authorId && String(currentUserId) === String(authorId);
        const isAdmin = user && user.role === "admin";

        if (!isAuthor && !isAdmin) {
          setUnauthorized(true);
          // optionally navigate away after showing message
          // navigate("/", { replace: true });
        }
      } catch (err) {
        console.error("Failed to fetch post:", err);
        setError("Failed to load post for editing.");
      } finally {
        if (mounted) setFetching(false);
      }
    };

    fetchPost();
    return () => { mounted = false; };
  }, [id, isEdit, user]);

  useEffect(() => {
    // redirect to login if not authenticated
    if (!isAuthenticated()) {
      navigate("/login");
    }
  }, [isAuthenticated, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (unauthorized) {
      alert("You are not authorized to edit this post.");
      return;
    }
    setLoading(true);
    setError("");
    try {
      if (isEdit) {
        // update existing post
        await api.put(`/apii/posts/${id}`, { title, content });
        alert("Post updated.");
        navigate(`/post/${id}`);
      } else {
        // create new post
        const res = await api.post("/posts", { title, content });
        const newId = res.data._id || res.data.id || res.data?.post?._id;
        alert("Post created.");
        navigate(`/post/${newId}`);
      }
    } catch (err) {
      console.error(err);
      const msg = err?.response?.data?.message || err.message || "Failed to save post.";
      setError(msg);
      alert(msg);
    } finally {
      setLoading(false);
    }
  };

  if (fetching) return <div className="container"><p>Loading post...</p></div>;

  return (
    <div className="container">
      <h2>{isEdit ? "Edit Post" : "Write a new post"}</h2>

      {unauthorized && (
        <div className="form" style={{marginBottom:12}}>
          <p style={{color:'crimson',margin:0}}>You are not authorized to edit this post.</p>
        </div>
      )}

      <form className="form" onSubmit={handleSubmit}>
        <label>Title</label>
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
          disabled={unauthorized}
        />

        <label>Content</label>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          rows="12"
          required
          disabled={unauthorized}
        />

        {error ? <p style={{color:'crimson'}}>{error}</p> : null}

        <div style={{display:'flex',gap:8,alignItems:'center'}}>
          <button type="submit" className="btn" disabled={loading || unauthorized}>
            {loading ? (isEdit ? "Updating..." : "Publishing...") : (isEdit ? "Update Post" : "Publish")}
          </button>

          <button
            type="button"
            className="btn ghost"
            onClick={() => navigate(isEdit ? `/post/${id}` : "/")}
            disabled={loading}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
