import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import api from "../api/axiosConfig";
import PostCard from "../components/PostCard";

export default function Home() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    api.get("/posts")
      .then((res) => setPosts(res.data || []))
      .catch((err) => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  return (
    <main>
      <div className="hero">
        <div>
          <h1>Thoughts, tutorials, and stories â€” written by you.</h1>
          <p>Browse latest posts, get inspired, and publish your ideas in seconds.</p>
        </div>
        <div className="cta">
          <Link to="/write" className="btn">Write a Post</Link>
        </div>
      </div>

      <section>
        <h2 style={{color:'white', margin:'8px 0 12px'}}>Latest posts</h2>
        {loading ? <p className="center" style={{color:'white'}}>Loading...</p> : null}
        <div className="posts-grid">
          {posts.map((p) => <PostCard key={p._id} post={p} />)}
          {posts.length === 0 && !loading ? <p className="center" style={{color:'white'}}>No posts yet.</p> : null}
        </div>
      </section>
    </main>
  );
}
