import React from "react";
import { Link } from "react-router-dom";

export default function PostCard({ post }) {
  const excerpt = post.content ? post.content.slice(0, 150) + (post.content.length > 150 ? "..." : "") : "";
  return (
    <div className="post-card">
      <h3>{post.title}</h3>
      <p className="meta">By {post.author?.username || "Unknown"} â€¢ {new Date(post.createdAt).toLocaleDateString()}</p>
      <p>{excerpt}</p>
      <Link to={`/post/${post._id}`} className="read-more">Read more</Link>
    </div>
  );
}
