import React, { useState, useContext, useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import api from "../api/axiosConfig";
import { UserContext } from "../context/UserContext";

export default function WritePost() {
  const { id } = useParams(); // optional post id for edit
  const isEdit = Boolean(id);
  const { user, isAuthenticated } = useContext(UserContext);
  const navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [loading, setLoading] = useState(false);
  const [fetching, setFetching] = useState(false);
  const [error, setError] = useState("");
  const [unauthorized, setUnauthorized] = useState(false);

  useEffect(() => {
    if (!isEdit) return;
    let mounted = true;
    const fetchPost = async () => {
      setFetching(true);
      setError("");
      try {
        const res = await api.get(`/api/posts/${id}`);
        if (!mounted) return;
        const post = res.data;
        setTitle(post.title || "");
        setContent(post.content || "");

        // Determine authorization: current user must be author or admin
        const author = post.author || {};
        const authorId = author._id || author.id || author;
        const currentUserId = user ? (user._id || user.id) : null;
        const isAuthor = currentUserId && authorId && String(currentUserId) === String(authorId);
        const isAdmin = user && user.role === "admin";

        if (!isAuthor && !isAdmin) {
          setUnauthorized(true);
        }
      } catch (err) {
        console.error("Failed to fetch post:", err);
        setError("Failed to load post for editing.");
      } finally {
        if (mounted) setFetching(false);
      }
    };

    fetchPost();
    return () => { mounted = false; };
  }, [id, isEdit, user]);

  useEffect(() => {
    // redirect to login if not authenticated
    if (!isAuthenticated()) {
      navigate("/login");
    }
  }, [isAuthenticated, navigate]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (unauthorized) {
      alert("You are not authorized to edit this post.");
      return;
    }
    setLoading(true);
    setError("");
    try {
      if (isEdit) {
        // update existing post
        await api.put(`/api/posts/${id}`, { title, content });
        alert("Post updated.");
        navigate(`/post/${id}`);
      } else {
        // create new post
        const res = await api.post("/api/posts", { title, content });
        const newId = res.data._id || res.data.id || res.data?.post?._id;
        alert("Post created.");
        navigate(`/post/${newId}`);
      }
    } catch (err) {
      console.error(err);
      const msg = err?.response?.data?.message || err.message || "Failed to save post.";
      setError(msg);
      alert(msg);
    } finally {
      setLoading(false);
    }
  };

  if (fetching) return <div className="container"><p>Loading post...</p></div>;

  return (
    <div className="container" style={{ maxWidth: 700, margin: "auto", padding: 20 }}>
      <h2 style={{ marginBottom: 20 }}>{isEdit ? "Edit Post" : "Write a new post"}</h2>

      {unauthorized && (
        <div style={{ marginBottom: 12 }}>
          <p style={{ color: "crimson", margin: 0 }}>
            You are not authorized to edit this post.
          </p>
        </div>
      )}

      <form
        onSubmit={handleSubmit}
        style={{
          display: "flex",
          flexDirection: "column",
          gap: 20
        }}
      >
        <div style={{ display: "flex", flexDirection: "column", gap: 6 }}>
          <label style={{ fontWeight: 600 }}>Title</label>
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            disabled={unauthorized}
            placeholder="Enter post title"
            style={{
              padding: "10px 12px",
              fontSize: 16,
              borderRadius: 6,
              border: "1px solid #ccc",
              width: "100%",
              boxSizing: "border-box",
              outline: "none"
            }}
          />
        </div>

        <div style={{ display: "flex", flexDirection: "column", gap: 6 }}>
          <label style={{ fontWeight: 600 }}>Content</label>
          <textarea
            value={content}
            onChange={(e) => setContent(e.target.value)}
            rows={12}
            required
            disabled={unauthorized}
            placeholder="Write your post here..."
            style={{
              padding: "12px",
              fontSize: 16,
              borderRadius: 6,
              border: "1px solid #ccc",
              width: "100%",
              boxSizing: "border-box",
              resize: "vertical",
              outline: "none"
            }}
          />
        </div>

        {error && <p style={{ color: "crimson", margin: 0 }}>{error}</p>}

        <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
          <button
            type="submit"
            disabled={loading || unauthorized}
            style={{
              padding: "12px",
              fontSize: 16,
              borderRadius: 6,
              border: "none",
              background: "linear-gradient(90deg, #FF6F00, #FFB74D)",
              color: "white",
              fontWeight: 600,
              cursor: "pointer"
            }}
          >
            {loading ? (isEdit ? "Updating..." : "Publishing...") : (isEdit ? "Update Post" : "Publish")}
          </button>

          <button
            type="button"
            onClick={() => navigate(isEdit ? `/post/${id}` : "/")}
            disabled={loading}
            style={{
              padding: "12px",
              fontSize: 16,
              borderRadius: 6,
              border: "1px solid #ccc",
              background: "#fff",
              cursor: "pointer"
            }}
          >
            Cancel
          </button>
        </div>
      </form>
    </div>
  );
}
